mapper.select:
		select *
 		from(
 			select rownum rnum, b.*
 			from(
 				select *
 				from book
 				order by ${sort} ${direction}
 			) b
 		) where rnum between #{queryStart} and #{queryEnd}

		select *
 		from(
 			select rownum rnum, b.*
 			from(
 				select *
 				from book
 				order by ${sort} ${direction}
 			) b
 		) where (rnum between #{queryStart} and #{queryEnd}) and (title like '%' || #{title} ||'%' and author like '%' || #{author} || '%')
