mapper.select:
		select *
 		from(
 			select rownum rnum, b.*
 			from(
 				select *
 				from book
 				order by ${sort} ${direction}
 			) b
 		) where rnum between #{queryStart} and #{queryEnd}

		select *
 		from(
 			select rownum rnum, b.*
 			from(
 				select *
 				from book
 				where title LIKE  '%'||#{title}||'%' and author LIKE '%'||#{author}||'%'
 				order by ${sort} ${direction}
 			) b
 		) where (rnum between #{queryStart} and #{queryEnd})
